function commandSet() {
	$name = ''; 
	$value = '';
	if(isset($_GET['name'])) {
		$name = $_GET['name'];
		if(isset($_GET['value'])) {
			$value = $_GET['value'];
		}
		$session = new Session(SESSION_NAME, false);
		$session->{$name}=$value;
	} 
	return json_encode(array('set' => array($name => $value), 'session' => $_SESSION));
}
function commandCreate() {
	$result = array('create' => 'nothing');

	if(isset($_GET['folder'])) {
		$foldername = $_GET['folder'];
		if( !file_exists(BROWSE_BASEDIR . $foldername) ) {
		    if( mkdir(BROWSE_BASEDIR . $foldername, 0777, true) ) { $result = array('create' => array('folder' => $foldername, 'result' => 'success')); } 
		    	else { $result = array('create' => array('folder' => $foldername, 'result' => 'error')); }
		}		
	} 
	return json_encode($result);
}

function runBash($command) {
	global $session;
	return shell_exec($session->{'bash_folder'} . '/' . $command);
}

