class Action {
	public $key;
	public $parentKey;
	public $title;
	public $result;
	public $visible;
	public $onClickScript;
	public $callbackScript;
	public $href;
	public $handler;

	public function __construct($key, $parentKey, $title, $result, $visible='always') {
		$this->key = $key;
		$this->parentKey = $parentKey;
		$this->title = $title;
		$this->result = $result;
		$this->visible = $visible;
		$this->onClickScript = "";
		$this->callbackScript = "";
		$this->href = "";
		$this->handler = null;
	}

    public function __call($method, $args)
    {
        if (isset($this->$method)) {
            $func = $this->$method;
            return call_user_func_array($func, $args);
        }
    }

    public function setOnClickScript($javascript) { $this->onClickScript = $javascript; return $this; }

    public function getOnClickScript() { return (strlen($this->onClickScript)>0)?"function " . $this->parentKey . '_' . $this->key . "OnClick() {\n" . $this->onClickScript . "\n}":""; }
    public function getOnClick() { return (strlen($this->onClickScript)>0)?" onClick=\"" . $this->parentKey . '_' . $this->key . "OnClick()\"":""; }

	public function setHref($url) { $this->href = $url; return $this; }
	public function getHref() { return (strlen($this->href)>0)?" href=\"" . $this->href . "\"":""; }

	public function setCallbackScript($javascript) { $this->callbackScript = $javascript; return $this; }
	public function getCallbackScript() { return (strlen($this->callbackScript)>0)?"function " . $this->parentKey . '_' . $this->key . "Callback() {\n" . $this->onClickScript . "\n}":""; }

	public function getA() {
		$ret = "<a";
		$ret .= " style=\"" . UpgradeTest::$v[$this->visible][$this->result] . "\"";
		$ret .= $this->getOnClick();
		$ret .= $this->getHref();
		$ret .= ">";
		$ret .= $this->title;
		$ret .= "</a>";
		return $ret;
	}

	public function setHandler($method) {
		$this->handler = $method;
		return $this;
	}
}

class UpgradeTest {
