		STATUS_NONE => array(STATUS_ERROR => "display:inline;", STATUS_WARNING => "display:inline;", STATUS_SUCCESS => "display:inline;"),
		STATUS_SUCCESS => array(STATUS_ERROR => "display:none;", STATUS_WARNING => "display:none;", STATUS_SUCCESS => "display:inline;"),
		STATUS_WARNING => array(STATUS_ERROR => "display:none;", STATUS_WARNING => "display:inline;", STATUS_SUCCESS => "display:none;"),
		STATUS_ERROR => array(STATUS_ERROR => "display:inline;", STATUS_WARNING => "display:none;", STATUS_SUCCESS => "display:none;")
	);
	public static $s = array(
		STATUS_NONE => array('class' => ""),
		STATUS_SUCCESS => array('class' => "statusSuccess"),
		STATUS_WARNING => array('class' => "statusWarning"),
		STATUS_ERROR => array('class' => "statusError")
	);

	public $key;
	public $description;
	public $helptext;
	public $errortext;
	public $warningtext;
	public $value;
	public $result;
	public $onChangeScript;
	public $actionList = array();

	public function __construct($key, $description) {
		$this->key = $key;
		$this->description = $description;
		$this->onChangeScript = "";
		$this->helptext = "";
		$this->errortext = "";
	}

	public function addAction($action) {
		$this->actionList[] = $action;
		return $action;
	}

	public function getActionByName($actionName) {
		foreach($actionList as $action) {
			if(strcasecmp($actionName, $action->title) == 0) {
				return $action;
			}
		}
		return null;
	}
	public function getAction($actionKey) {
		foreach($this->actionList as $action) {
			if(strcasecmp($actionKey, $action->key) == 0) {
				return $action;
			}
		}
		return null;
	}

	public function getValue() { return "<div id=\"" . $this->key . '.value' . "\">" . $this->value . "</div>"; }

	public function setOnChangeScript($javascript) { $this->onChangeScript = $javascript; return $this; }
	public function getOnChangeScript() { return (strlen($this->onChangeScript)>0)?"function " . $this->key . "OnChange(value) {\n" . $this->onChangeScript . "\n}":""; }

	public function getA() { $ret = ''; foreach ($this->actionList as $action) { $ret .= ' ' . $action->getA(); } return $ret; }
	public function getS() { return " class=\"" . UpgradeTest::$s[$this->result]['class'] . "\""; }

	public function getJavaScripts() {
		$ret = "";
		$ret .= "\n" . $this->getOnChangeScript(); 
		foreach ($this->actionList as $action) {
			$ret .= "\n" . $action->getOnClickScript();
		}
		return $ret;		
	}
}

//####################################################
//#####  Session data  ###############################
//####################################################
class Session {

	private $variables = array();
	private $sessionName;
	private $autoclose;

	public function __construct($session = 'MALMANGERSESSID', $autoclose = true) {
		global $defaultValue;

		$this->autoclose = $autoclose;
		$this->sessionName = $session;
		session_name($this->sessionName);
		session_start();
		// get all default variables
		foreach ($defaultValue as $key => $value) { $this->variables[$key] = $value; }
		// get all session data
		foreach ($_SESSION as $key => $value) { $this->variables[$key] = $value; }
		// finish session
		if ($this->autoclose) { session_write_close(); }
	}
	public function __destruct() {
		if (!$this->autoclose) { session_write_close(); }	
	}

	public function __get($name) { $ret = null; if (isset($this->variables[$name])) { $ret = $this->variables[$name]; } return $ret; }
	public function __set($name, $value) { 
		$this->variables[$name] = $value; 
		if(!$this->autoclose) { $_SESSION[$name]=$value; }
	}
	public function __isset($name) { return isset($this->variables[$name]); }
	public function __unset($name) { unset($this->variables[$name]); }

	public function getDebug() { return "sessionName=" . $this->sessionName . " - " . print_r($this->variables, true) . " - " . print_r($_SESSION, true); }

}

class Http {

	public static function response400($message = 'Bad Request') {
		header('HTTP/1.1 400 ' . $message, true, 400);
		header("Cache-Control: no-store, no-cache, must-revalidate, max-age=0");
		header("Cache-Control: post-check=0, pre-check=0", false);
		header("Pragma: no-cache");		
		echo "<html><head><title>400 " . $message . "</title></head><body><h1>400 " . $message . "</h1></body></html>\n"; 
		exit();
	}

	public static function response200($message = 'OK', $response = '') {
		header('HTTP/1.1 200 ' . $message, true, 200);
		header("Cache-Control: no-store, no-cache, must-revalidate, max-age=0");
		header("Cache-Control: post-check=0, pre-check=0", false);
		header("Pragma: no-cache");		
		echo $response;
		exit();
	}
}
